    overflow: hidden;

}
.image{
    max-width: 600px;

    position: absolute;
    top: 50%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%)
}
.image:active{
    transition: 1.5s;
    max-width: 100%;
    max-height: 100%;
}
.text{
    position: absolute;
    top: 9%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%)
}
.text2{
    display: none;
    position: absolute;
    top: 10%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%)
}
.input{

}
.input1{
    position: fixed;
    background: azure;
    width: 200px;
    height: 29px;
    border-radius:4px ;
    opacity: 0.7;
    top: 20%;
    left: 50%;
