* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    outline: none;
}

.main {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    padding: 20px;
    overflow: hidden;
    transform: translateY(30px) translateX(-160px);
}

.settings {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 200px;
}

.slider {
    width: 100%;
    color: #cccccc;
}
.iconLeft{

}
.iconLeft:hover{
    opacity: 0.8;
}
.slider input {
    width: 100%;
}
.slider2{
    overflow: hidden;
    -webkit-appearance: none;
    height: 7px;
    background-color: #6E6E6E;
    border-radius: 10px;

}
